'use strict';

const config = require('./config.json');
const pubsub = require('@google-cloud/pubsub')();
const topic = pubsub.topic(config.RESULT_TOPIC);
const maxSkew = config.MAX_SKEW;
const publisher = topic.publisher({
	batching: {
	  maxMessages: 10,
	  maxMilliseconds: 5000
	}
});

const emptyPNG  = new Buffer('iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNkYPhfDwAChwGA60e6kgAAAABJRU5ErkJggg==', 'base64');
const emptyGIF  = new Buffer('R0lGODlhAQABAIAAAP///wAAACH5BAEAAAAALAAAAAABAAEAAAICRAEAOw==', 'base64');
const emptyMP3  = new Buffer('', 'base64');
const emptySVG  = new Buffer('', 'base64');
const emptySWF  = new Buffer('Q1dTCGYAAAB4nKtgYI1nYOBfwMDAw8jgIsgABM5M/4GAkwPIZARCByBtzycH5QUk5qXmxJdkluSkOiUmZ6cX5ZfmpQRnZ+Yx9LGxgRQc/f/h/3/T6L6JIH0ApWwYMg==', 'base64');
const emptyJS   = new Buffer('// nocontent\n');
const emptyTXT  = new Buffer('nocontent\n');
const emptyCSS  = new Buffer('/* nocontent */\n');
const emptyMP4  = new Buffer('', 'base64');
const emptyOGG  = new Buffer('', 'base64');
const emptyWEBM = new Buffer('', 'base64');
const emptyFLV  = new Buffer('RkxWAQAAAAAJAAAAAA==', 'base64');


/**
 * Background Cloud Function to be triggered by Pub/Sub.
 *
 * @param {object} event The Cloud Functions event.
 * @param {function} callback The callback function.
 */
exports.beaconEvent = (message, callback) => {
  const data = message.data;
  const requestData = JSON.parse(data ? Buffer.from(data, 'base64').toString() : '{}');

  console.log(`Hello, ${requestData}!`);

  message.ack();

  callback();
};


/**
 * Publishes the result to the given pubsub topic and returns a Promise.
 *
 * @param {string} topicName Name of the topic on which to publish.
 * @param {object} data The message data to publish.
 */
function publishResult (data) {
	const encodedData = new Buffer.from(JSON.stringify(data));
	return publisher.publish(encodedData).then(id => console.log('id', id))
}


/**
 * HTTP Cloud Function.
 *
 * @param {Object} req Cloud Function request context.
 * @param {Object} res Cloud Function response context.
 */
exports.beacon = (req, res) => {
	res.set('Access-Control-Allow-Origin', '*');

	let url = req.url;
	var filename = url.substring(url.lastIndexOf('/')+1);
	if (filename == null || filename == undefined) {
		return res.status(404).send('Not found').end();
	}
	if (filename.lastIndexOf('?') > 0) {
		filename = filename.substring(0, filename.lastIndexOf('?'));
	}

	let fileArr = filename.split('.');
	let fileType = fileArr[0];
	if (fileType == 'data') {
		fileType = 'e';
	}
	if (fileType != 'e' && fileType != 'b') {
		return res.status(404).send('Not found').end();
	}

	let requestTime = new Date(parseInt(req.query['_rt'])).getTime();
	let currentTime = Date.now();
	let startedTime = new Date(parseInt(req.query['_st'])).getTime();
	let skew = currentTime - requestTime;
	if (requestTime == 0 || skew/1000 > maxSkew) {
		return res.status(400).send('Bad request').end();
	}

	let token = req.query['_id'] || '';
	if (token === '') {
		return res.status(400).send('Bad request').end();
	}

	// remove the id
	delete req.query['_rt']
	delete req.query['_tk']

	let body = {}
	if (req.headers['content-type'] == "application/json") {
		body = req.body;
	}

	const requestData = {
		rawHeaders: req.rawHeaders,
		headers: req.headers,
		originalUrl: req.originalUrl,
		url: url,
		query: req.query,
		path: req.path,
		params: req.params,
		method: req.method,
		httpVersion: req.httpVersion,
		hostname: req.hostname,
		secure: req.secure,
		protocol: req.protocol,
		cookies: req.cookies,
		ip: req.ip,
		ips: req.ips,
		data: body,
		time: {
			request: requestTime,
			current: currentTime,
			started: startedTime,
			skew: skew,
		},
		token: token,
	};
	

	var extension = 'txt';
	if (fileArr.length > 1) {
		extension = fileArr[1];
	}

	let contentType = "text/plain";
	let data = emptyTXT;

	switch (extension) {
		case "gif":
			contentType = "image/gif";
			data = emptyGIF;
			break;

		case "png":
			contentType = "image/png";
			data = emptyPNG;
			break;

		case "js":
			contentType = "application/javascript";
			data = emptyJS;
			break;

		case "css":
			contentType = "text/css";
			data = emptyCSS;
			break;

		case "svg":
			contentType = "image/svg+xml";
			data = emptySVG;
			break;

		case "mp3":
			contentType = "audio/mpeg";
			data = emptyMP3;
			break;

		case "swf":
			contentType = "application/x-shockwave-flash";
			data = emptySWF;
			break;

		case "m4v":
		case "mp4":
			contentType = "video/mp4";
			data = emptyMP4;
			break;

		case "ogg":
			contentType = "video/ogg";
			data = emptyOGG;
			break;

		case "webm":
			contentType = "video/webm";
			data = emptyWEBM;
			break;

		case "flv":
			contentType = "video/x-flv";
			data = emptyFLV;
			break;

		default:
	}

	res.set('Content-Type', contentType);
	if (fileType == 'e') {
		res.status(204);
	} else {
		res.status(200).send(data);
	}
	return publishResult(requestData);
};